def al2o3(*, wavelength: Any) -> Any:
    """Returns the index of refraction of Al2O3 synthetic sapphire, ordinary ray (o), 20 C in complex form. (http://refractiveindex.info/?group=CRYSTALS&material=Al2O3)

    Args:
        wavelength (ndarray): Wavelength range of interest. (list, float, integer or numpy arange, range)
            valid range: 200 <= x <= 5000 nm

    Returns:
        (ndarray): Complex index of refraction of Al2O3.
    """
    if isinstance(wavelength, (list, np.arange, np.array, range)):
        if 200 <= wavelength <= 5000:
            _x = wavelength*1-3
            c_1, c_2, c_3 = 1.4313493, 0.65054713, 5.3414021
            b_1, b_2, b_3 = 0.0726631, 0.1193242, 18.028251
            n = np.sqrt(1.0 + c_1/(1.0 - (b_1/_x)**2) + c_2/(1.0 - (b_2/_x)**2) + c_3/(1.0 - (b_3/_x)**2))
            n += 1j*0.0

            return n.reshape(-1)

    raise ValueError("the input wavelength_oi is out of bounds.")

